mapper.select:
select *
from check_homework


        AND case_name LIKE CONCAT ('%',#{caseName},'%')


        and stage=#{stage}


        and sub_ject=#{subject}


        and flag=#{flag}

mapper.update:
update check_homework
set case_name = #{caseName}, expect_value=#{expectValue}, request_body =#{requestBody}
where id = #{id}
